import { announcements } from '@/lib/data';
import { notFound } from 'next/navigation';
import { Badge } from '@/components/ui/Badge';
import { Calendar, Tag, ArrowLeft } from 'lucide-react';
import Link from 'next/link';

export async function generateStaticParams() {
  return announcements.map((ann) => ({
    id: ann.id,
  }));
}

export default async function AnnouncementDetailPage({
  params,
}: {
  params: Promise<{ id: string }>;
}) {
  const { id } = await params;
  const announcement = announcements.find((a) => a.id === id);

  if (!announcement) {
    notFound();
  }

  return (
    <div className="mx-auto max-w-4xl px-6 py-12 lg:px-8">
      <Link href="/elanlar" className="inline-flex items-center text-sm text-gray-500 hover:text-blue-600 mb-8 transition-colors">
        <ArrowLeft className="h-4 w-4 mr-2" />
        Elanlara qay─▒t
      </Link>

      <article className="bg-white rounded-2xl shadow-sm border border-gray-100 p-8 md:p-12 overflow-hidden">
        <div className="flex gap-2 mb-6">
          {announcement.tags.map(tag => (
            <Badge key={tag} variant="secondary" className="px-3 py-1 text-sm bg-orange-50 text-orange-700">
              {tag}
            </Badge>
          ))}
        </div>

        <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-6 leading-tight">
          {announcement.title}
        </h1>

        <div className="flex items-center text-gray-500 mb-8 pb-8 border-b border-gray-100">
          <Calendar className="h-5 w-5 mr-2" />
          <span className="font-medium">{announcement.date}</span>
        </div>

        <div className="prose prose-blue max-w-none text-gray-600 leading-relaxed">
          <p className="text-lg text-gray-900 font-medium mb-6">
            {announcement.excerpt}
          </p>
          <div className="whitespace-pre-wrap">
            {announcement.content}
          </div>
        </div>
      </article>
    </div>
  );
}

'use client';

import { useState, useMemo } from 'react';
import { materials, conferences } from '@/lib/data';
import { Card, CardContent } from '@/components/ui/Card';
import { Badge } from '@/components/ui/Badge';
import { Button } from '@/components/ui/Button';
import { Select } from '@/components/ui/Select';
import { Input } from '@/components/ui/Input';
import { FileText, Download, Search, Book, File } from 'lucide-react';
import Link from 'next/link';

export default function MaterialsPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedYear, setSelectedYear] = useState('all');
  const [selectedType, setSelectedType] = useState('all');
  const [selectedConference, setSelectedConference] = useState('all');

  // Derived options
  const years = useMemo(() => Array.from(new Set(materials.map(m => m.year))).sort((a, b) => b - a), []);
  const types = useMemo(() => Array.from(new Set(materials.map(m => m.type))).sort(), []);
  
  // Filter logic
  const filteredMaterials = materials.filter(material => {
    const matchesSearch = material.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesYear = selectedYear === 'all' || material.year.toString() === selectedYear;
    const matchesType = selectedType === 'all' || material.type === selectedType;
    const matchesConference = selectedConference === 'all' || material.conferenceId === selectedConference;
    return matchesSearch && matchesYear && matchesType && matchesConference;
  });

  // Helper to get conference title
  const getConferenceTitle = (id?: string) => {
    if (!id) return null;
    return conferences.find(c => c.id === id)?.title;
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'Program': return <FileText className="h-5 w-5" />;
      case 'Book': return <Book className="h-5 w-5" />;
      default: return <File className="h-5 w-5" />;
    }
  };

  return (
    <div className="mx-auto max-w-7xl px-6 py-12 lg:px-8">
      <div className="mb-10">
        <h1 className="text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl mb-4">
          Konfrans Materiallar─▒
        </h1>
        <p className="text-lg text-gray-600">
          Konfrans proqramlar─▒, tezis kitablar─▒ v╔Ö dig╔Ör r╔Ösmi s╔Ön╔Ödl╔Ör.
        </p>
      </div>

      {/* Filters */}
      <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 mb-8 space-y-4">
        <div className="relative">
             <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
             <Input 
               placeholder="S╔Ön╔Öd ad─▒ ├╝zr╔Ö axtar─▒┼ƒ..." 
               className="pl-9"
               value={searchTerm}
               onChange={(e) => setSearchTerm(e.target.value)}
             />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Select 
            value={selectedConference} 
            onChange={(e) => setSelectedConference(e.target.value)}
          >
            <option value="all">B├╝t├╝n Konfranslar</option>
            {conferences.map(conf => (
              <option key={conf.id} value={conf.id}>{conf.title}</option>
            ))}
          </Select>

          <Select 
            value={selectedType} 
            onChange={(e) => setSelectedType(e.target.value)}
          >
            <option value="all">B├╝t├╝n s╔Ön╔Öd tipl╔Öri</option>
            {types.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </Select>

          <Select 
            value={selectedYear} 
            onChange={(e) => setSelectedYear(e.target.value)}
          >
            <option value="all">B├╝t├╝n ill╔Ör</option>
            {years.map(year => (
              <option key={year} value={year}>{year}</option>
            ))}
          </Select>
        </div>
      </div>

      {/* Material List */}
      <div className="grid gap-4">
        {filteredMaterials.length > 0 ? (
          filteredMaterials.map((material) => (
            <Card key={material.id} className="hover:border-blue-300 transition-colors">
              <CardContent className="p-6 flex flex-col sm:flex-row items-center gap-6">
                <div className="h-12 w-12 bg-blue-100 rounded-full flex items-center justify-center text-blue-600 shrink-0">
                  {getTypeIcon(material.type)}
                </div>
                
                <div className="flex-1 space-y-1 text-center sm:text-left">
                  <div className="flex flex-wrap justify-center sm:justify-start gap-2 mb-2">
                    <Badge variant="outline" className="text-xs">{material.type}</Badge>
                    <Badge variant="secondary" className="text-xs">{material.year}</Badge>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900">{material.title}</h3>
                  {material.conferenceId && (
                    <p className="text-sm text-gray-500 line-clamp-1">
                      {getConferenceTitle(material.conferenceId)}
                    </p>
                  )}
                  {material.note && (
                    <p className="text-xs text-gray-400 italic">
                      {material.note}
                    </p>
                  )}
                </div>

                <a href={material.pdfUrl} className="shrink-0 w-full sm:w-auto">
                  <Button className="w-full gap-2 bg-blue-600 hover:bg-blue-700">
                    <Download className="h-4 w-4" />
                    Y├╝kl╔Ö
                  </Button>
                </a>
              </CardContent>
            </Card>
          ))
        ) : (
          <div className="text-center py-20 bg-gray-50 rounded-xl border border-dashed border-gray-300">
            <p className="text-gray-500">He├º bir material tap─▒lmad─▒.</p>
          </div>
        )}
      </div>
    </div>
  );
}

import { cn } from '@/lib/utils';
import { InputHTMLAttributes, forwardRef } from 'react';

export interface InputProps extends InputHTMLAttributes<HTMLInputElement> {}

const Input = forwardRef<HTMLInputElement, InputProps>(
  ({ className, type, ...props }, ref) => {
    return (
      <input
        type={type}
        className={cn(
          "flex h-10 w-full rounded-md border border-gray-300 bg-white px-3 py-2 text-sm placeholder:text-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:cursor-not-allowed disabled:opacity-50 transition-all",
          className
        )}
        ref={ref}
        {...props}
      />
    );
  }
);
Input.displayName = "Input";

export { Input };

import * as React from "react"
import { cn } from "@/lib/utils"
import { ChevronDown } from "lucide-react"

export interface SelectProps extends React.SelectHTMLAttributes<HTMLSelectElement> {}

const Select = React.forwardRef<HTMLSelectElement, SelectProps>(
  ({ className, children, ...props }, ref) => {
    return (
      <div className="relative">
        <select
          className={cn(
            "flex h-10 w-full appearance-none rounded-md border border-gray-300 bg-white px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:cursor-not-allowed disabled:opacity-50 pr-8 transition-all",
            className
          )}
          ref={ref}
          {...props}
        >
          {children}
        </select>
        <ChevronDown className="absolute right-2.5 top-3 h-4 w-4 opacity-50 pointer-events-none" />
      </div>
    )
  }
)
Select.displayName = "Select"

export { Select }
